INSERT INTO `staffcomment` (`BookID`, `StaffComment`) VALUES
(1, 'Would you want to live forever? Doesn\'t that depend on what sort of life you\'d have—and who you\'d spend it with?'),
(3, '\"This graphic novel is better than years of marriage counceling\"'),
(5, 'Alanna didn\'t expect this Tuesday to be any different than the Tuesday before, and the Tuesday before that. She was wrong. Can she make it to Wednesday?'),
